function [xpts,xpts2] = absi(ndft,stepp,frequency)% computes abscissas used in plotting signals in the frequency domain% xpts in Hz % xpts2 in ppm    xlow  = -1000 / (2*stepp) ;    xstepp = 1000 / (ndft*stepp);    xupp = -xlow;    xpts     = xlow:xstepp:xupp;      freqref=0;    ppmref=4.7;    if nargin <3         xpts2  = ppmref+1000*xpts./(63.86);    else        sfreq = frequency*1000;        xpts2=(xpts-freqref)/sfreq*10^6+ppmref;     end        % xlow   = -1 / (2 * stepp);% xstepp = 1 / (ndft * stepp);% xupp   = -xlow;% xpts   = (xlow:xstepp:xupp);% xpts2  = 4.67+1000*xpts./(63.86);